    password: Joi.string().required()

  });
  const { error, value } = schema.validate(req.body);
  if (error) {
    res.status(HttpStatus.BAD_REQUEST).json({
      code: HttpStatus.BAD_REQUEST,
      message: '${error}'
  });
 }
  else {

    next();
  }
